DROP TABLE IF EXISTS `wisata_galeri`;
CREATE TABLE `wisata_galeri` (
  `wisata_galeri_id` int(11) NOT NULL AUTO_INCREMENT,
  `nama_foto` varchar(45) DEFAULT NULL,
  `url_foto` varchar(255) DEFAULT NULL,
  `deskripsi_foto` varchar(120) DEFAULT NULL,
  `stt` int(11) DEFAULT NULL,
  `date_add` date DEFAULT NULL,
  `time_add` time DEFAULT NULL,
  `wisata_id` int(11) NOT NULL,
  PRIMARY KEY (`wisata_galeri_id`),
  KEY `fk_wisata_galeri_wisata_idx` (`wisata_id`),
  CONSTRAINT `fk_wisata_galeri_wisata` FOREIGN KEY (`wisata_id`) REFERENCES `wisata` (`wisata_id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=latin1;

